INSERT INTO shift_schedules(employee_id, date, scheduled_start, scheduled_end, created_at) VALUES
(#{s.employeeId}, #{s.start}, #{s.scheduledStart}, #{s.scheduledEnd},now())
;

mapper.delete:
DELETE FROM
  shift_requests
WHERE
  date NOT IN (

	  #{r.start}

  )
  AND employee_id=#{employeeId}
;

DELETE FROM
  shift_schedules
WHERE
  (date,employee_id) NOT IN (

	  (#{s.start},#{s.employeeId})

  )
  AND MONTH(date)=#{targetMonth}
;
