--シフトは4月とまったく一緒なので、4月の勤務履歴をそのまま挿入。
INSERT INTO
	time_records(employee_id,date,clock_in,clock_out)
SELECT
	employee_id
	,date + INTERVAL '2' MONTH
	,clock_in
	,clock_out
	 FROM time_records
	 WHERE date + INTERVAL '2' MONTH<CURRENT_DATE
	 AND MONTH(date)=4;